export const Icon: React.FC<IconProps> = ({ 
  name, 
  className = '', 
  size = 'md' 
}) => {
  const iconPath = icons[name as keyof typeof icons];
  
  if (!iconPath) {
    console.warn(`Icon "${name}" not found`);
    return null;
  }

  return (
    <svg 
      className={`${iconSizes[size]} ${className}`}
      fill="none" 
      viewBox="0 0 24 24" 
      xmlns="http://www.w3.org/2000/svg"
    >
      {iconPath}
    </svg>
  );
};

export default Icon;